import React,{useState} from "react";

function Login() {
  const [user,setUser]=useState({
    username:"",
    password:""
  });
  let name,value;
const handleInputs=(e)=>{
  console.log(e);
  name=e.target.username;
  value=e.target.value;
  setUser({...user,[name]:value})
}

  return (
    <div>
    <form action=""  method="POST">
        <div><img class="img_job" src="https://th.bing.com/th/id/OIP.fC1wJpC_gAgf8djU_I07OgHaFj?w=256&h=192&c=7&r=0&o=5&pid=1.7" /></div>
        <div><input placeholder="USERNAME" name="name" value={user.username} onChange={handleInputs} type="text" /></div>
        <div><input placeholder="PASSWORD" name="password" value={user.password} onChange={handleInputs} type="password" /></div>
        <button type="button" class="btn btn-outline-success">
          Login
        </button>
      </form>
    </div>
  );
}
export default Login;
