import React, {
  useState
} from "react";
import axios from "axios";
import {useHistory} from "react-router-dom"


function Login({setLoginUser}) {

const history=useHistory()

  const [user, setUser] = useState({
    username: "",
    password: ""
  });
  let name, value;
  const handleInputs = (e) => {
    console.log(e);
    name = e.target.name;
    value = e.target.value;
    setUser({
      ...user,
      [name]: value
    })
  }
const button=()=>{
  const{username,password}=user
  if(username&&password){
    alert("posted");
  axios.post("http://localhost:3000/loginc",user)
  .then(res=>{
    alert(res.data.message)
    setLoginUser(res.data.user)
    history.push("/")
  })
}else{
  alert("invalid input")
}
}


  return ( <
    div >
    <
    form action = ""
    method = "POST" >
    <
    div > < img class = "img_job"
    src = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSz5vHWAaLILcjvciGxEGlmJ89AC_gTh_k5ag&usqp=CAU" / > < /div> <
    div > < input placeholder = "USERNAME"
    name = "username"
    value = {
      user.username
    }
    onChange = {
      handleInputs
    }
    type = "text" / > < /div> <
    div > < input placeholder = "PASSWORD"
    name = "password"
    value = {
      user.password
    }
    onChange = {
      handleInputs
    }
    type = "password" / > < /div> <
    button type = "button"
    onClick={button}
    class = "btn btn-outline-success" >
    Login <
    /button> <
    /form> <
    /div>
  );
}
export default Login;
