import React, {
  useState
} from "react";
import axios from "axios";
function Login() {
  const [user, setUser] = useState({
    username: "",
    password: ""
  });
  let name, value;
  const handleInputs = (e) => {
    console.log(e);
    name = e.target.name;
    value = e.target.value;
    setUser({
      ...user,
      [name]: value
    })
  }
const button=()=>{
  const{username,password}=user
  if(username&&password){
    alert("posted");
  axios.post("http://localhost:3000/loginc",user)
  .then(res=>console.log(res))
}else{
  alert("invalid input")
}
}


  return ( <
    div >
    <
    form action = ""
    method = "POST" >
    <
    div > < img class = "img_job"
    src = "https://www.google.com/imgres?imgurl=https%3A%2F%2Fwww.clipartmax.com%2Fpng%2Fmiddle%2F185-1854893_tn-police-department-logo-tamil-nadu-police-flag.png&imgrefurl=https%3A%2F%2Fwww.clipartmax.com%2Fmiddle%2Fm2i8b1K9H7m2H7i8_tn-police-department-logo-tamil-nadu-police-flag%2F&tbnid=jy3McghvSc8D_M&vet=12ahUKEwi787as0af5AhVVgmMGHWa5DTUQMygZegUIARD7AQ..i&docid=-HAyAfQwD2UwJM&w=840&h=510&q=police%20department%20login%20page%20logo&ved=2ahUKEwi787as0af5AhVVgmMGHWa5DTUQMygZegUIARD7AQ" / > < /div> <
    div > < input placeholder = "USERNAME"
    name = "username"
    value = {
      user.username
    }
    onChange = {
      handleInputs
    }
    type = "text" / > < /div> <
    div > < input placeholder = "PASSWORD"
    name = "password"
    value = {
      user.password
    }
    onChange = {
      handleInputs
    }
    type = "password" / > < /div> <
    button type = "button"
    onClick={button}
    class = "btn btn-outline-success" >
    Login <
    /button> <
    /form> <
    /div>
  );
}
export default Login;
